import './App.css';
import React, { useState, useEffect } from 'react';

function App() {

  const randonNumber = Math.floor(Math.random()*(100)+ 1);

  const [number, setnumber] = useState(1);

  const handleRandomNumber = () => randonNumber;

  useEffect(() => {
    setInterval(handleRandomNumber, 1000);
  });

  return (
    <div className="App">
      {number}
    </div>
  );
}

export default App;
